import * as m3o from "@m3o/m3o-node";
export class StreamService {
    constructor(token) {
        this.client = new m3o.Client({ token: token });
    }
    // Create a channel with a given name and description. Channels are created automatically but
    // this allows you to specify a description that's persisted for the lifetime of the channel.
    createChannel(request) {
        return this.client.call("stream", "CreateChannel", request);
    }
    // List all the active channels
    listChannels(request) {
        return this.client.call("stream", "ListChannels", request);
    }
    // List messages for a given channel
    listMessages(request) {
        return this.client.call("stream", "ListMessages", request);
    }
    // Send a message to the stream.
    sendMessage(request) {
        return this.client.call("stream", "SendMessage", request);
    }
}
